var mongoose = require('mongoose'); 
var ad = require('config/models');  

exports.postAd = function(title, content, category, author, callback) {

    var date = new Date();

    var newAd = new ad({    
            title: title,
            content : content,
            category : category,
            author : author,
            timePosted : date   
        });

    newAd.save(function (err) {
        callback({
            'response': "Ad sucessfully posted",
            'success': true
        });
    });
} 
